import { Table, Space } from "antd";
import {
  CheckCircleTwoTone,
  EditOutlined,
  DeleteOutlined,
  UserOutlined,
  VideoCameraOutlined,
  WechatOutlined,
} from "@ant-design/icons";
import { withRouter } from "react-router-dom";
import BaseTable from "../../shared/components/BasicTable";
import ActionUserTable from "./ActionsUsersTable";

const UsersTable = ({
  selectedUser,
  setSelectedUser,
  setPage,
  history,
  users,
  refetch,
  initialValues,
  handleDetails,
  handleEdit,
  userId,
  handleArchived,
  t,
  handleInactive,
  ...restProps
}) => (
  <>
    <BaseTable {...restProps} dataSource={users} refetch={refetch}>
      <Table.Column
        width="5%"
        title={t("actions")}
        dataIndex="actions"
        key="actions"
        render={(_, data) => (
          <div className="actions-column">
            <Space size="small">
              <a href="#!" onClick={() => handleEdit(data)} role="button" tabIndex={0}>
                <EditOutlined />{" "}
              </a>
              <a href="#!" onClick={() => handleArchived(data)} role="button" tabIndex={0}>
                <DeleteOutlined />
              </a>
              <a href="#!" onClick={() => handleInactive(data)} role="button" tabIndex={0}>
                {data.active ? (
                  <CheckCircleTwoTone twoToneColor="#FF0A50" />
                ) : (
                  <CheckCircleTwoTone twoToneColor="#52c41a" />
                )}
              </a>
              <a href="#!" onClick={() => handleDetails(data)} role="button" tabIndex={0}>
                <UserOutlined />
              </a>
            </Space>
          </div>
        )}
      />
      <Table.Column title={t("user")} dataIndex="user" key="user" sorter responsive={["lg"]} />
      <Table.Column title={t("channel")} dataIndex="channel" key="channel" sorter responsive={["lg"]} />
      <Table.Column
        title={t("state")}
        dataIndex="state"
        key="state"
        sorter
        responsive={["lg"]}
        render={(value) => {
          if (value === "0") {
            return "Inactive";
          }
          if (value === "1") {
            return "Waiting";
          }
          if (value === "2") {
            return "in Use";
          }
        }}
      />
      <Table.Column title={t("people")} dataIndex="people" key="people" sorter responsive={["lg"]} />
      <Table.Column
        title={t("mode")}
        dataIndex="mode"
        key="mode"
        sorter
        responsive={["lg"]}
        render={(value) =>
          value === "zoom" ? (
            <VideoCameraOutlined twoToneColor="#52c41a" style={{ fontSize: 14 }} /> + `Video`
          ) : (
            <WechatOutlined twoToneColor="#52c41a" style={{ fontSize: 14 }} />
          )
        }
      />
    </BaseTable>
  </>
);

export default withRouter(UsersTable);
