import { Table, Space } from "antd";
import { CheckCircleTwoTone, EditOutlined, DeleteOutlined, UserOutlined } from "@ant-design/icons";
import { withRouter } from "react-router-dom";
import BaseTable from "../../shared/components/BasicTable";
import ActionUserTable from "./ActionsUsersTable";

const UsersTable = ({
  selectedUser,
  setSelectedUser,
  setPage,
  history,
  users,
  refetch,
  initialValues,
  handleDetails,
  handleEdit,
  userId,
  handleArchived,
  t,
  handleInactive,
  ...restProps
}) => (
  <>
    <BaseTable {...restProps} dataSource={users} refetch={refetch}>
      <Table.Column
        width="5%"
        title={t("actions")}
        dataIndex="actions"
        key="actions"
        render={(_, data) => (
          <div className="actions-column">
            <Space size="small">
              <a href="#!" onClick={() => handleEdit(data)} role="button" tabIndex={0}>
                <EditOutlined />{" "}
              </a>
              <a href="#!" onClick={() => handleArchived(data)} role="button" tabIndex={0}>
                <DeleteOutlined />
              </a>
              <a href="#!" onClick={() => handleInactive(data)} role="button" tabIndex={0}>
                {data.active ? (
                  <CheckCircleTwoTone twoToneColor="#FF0A50" />
                ) : (
                  <CheckCircleTwoTone twoToneColor="#52c41a" />
                )}
              </a>
              <a href="#!" onClick={() => handleDetails(data)} role="button" tabIndex={0}>
                <UserOutlined />
              </a>
            </Space>
          </div>
        )}
      />
      <Table.Column
        width="15%"
        title={t("user")}
        dataIndex="user"
        key="user"
        sorter
        render={(text, row) => `${row.firstname} ${row.lastname}`}
      />
      ;
      <Table.Column width="25%" title={t("email")} dataIndex="email" key="email" sorter responsive={["lg"]} />
      <Table.Column width="10%" title={t("document")} dataIndex="document" key="document" sorter responsive={["lg"]} />
      <Table.Column
        width="10%"
        title={t("active")}
        dataIndex="active"
        key="active"
        sorter
        responsive={["lg"]}
        render={(value) => value && <CheckCircleTwoTone twoToneColor="#52c41a" style={{ fontSize: 14 }} />}
      />
      <Table.Column responsive={["lg"]} width="10%" title={t("role")} dataIndex="role" key="role" sorter />
    </BaseTable>
  </>
);

export default withRouter(UsersTable);
